    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        float fahre = (float) (32 + 1.8 * (Float.parseFloat(txbCelsius.getText())));
        float kelvin = (float) (273 + (Float.parseFloat(txbCelsius.getText())));
        
        txbFahrenheit.setText(String.valueOf(fahre));
        txbKelvin.setText(String.valueOf(kelvin));
    }//GEN-LAST:event_btnCalcularActionPerformed

